import React, { useState } from 'react';
import './Expenses.css'
import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import ExpensesFilter from './ExpensesFilter';

function Expenses(props) {
    const [expenses, setFilteredExpenses] = useState(props.expense);
    const [yearFilterBy, setYearFilterby] = useState('2020');
    const changeYear = (newYear) => {
        setYearFilterby(newYear);
        console.log(newYear);
    }
    const filteredExpense = props.expense.filter(itemInList => { return itemInList.date.getFullYear().toSting() === yearFilterBy }
    );
    return (
        <>
            <Card className='expenses'><ExpensesFilter onToggle={changeYear} />
                {

                    filteredExpense.map(item => <ExpenseItem key={item.id} date={item.date} title={item.title} amount={item.amount} />)
                }
            </Card>
        </>
    )
}

export default Expenses;
